import React, { useState } from 'react';
import { User, Task, TaskType } from '../types';
import { AVAILABLE_TASKS } from '../constants';
import AdSenseSlot from '../components/AdSenseSlot';

interface TasksProps {
  user: User;
  onUpdateUser: (user: User) => void;
}

const Tasks: React.FC<TasksProps> = ({ user, onUpdateUser }) => {
  const [activeTask, setActiveTask] = useState<Task | null>(null);
  const [timeLeft, setTimeLeft] = useState(0);
  const [isWatching, setIsWatching] = useState(false);
  const [completedTasks, setCompletedTasks] = useState<string[]>([]);

  const startTask = (task: Task) => {
    setActiveTask(task);
    setTimeLeft(task.durationSeconds);
    setIsWatching(true);

    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          completeTask(task);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const completeTask = (task: Task) => {
    setIsWatching(false);
    setCompletedTasks([...completedTasks, task.id]);

    const updatedUser = {
      ...user,
      balance: user.balance + task.reward,
      totalTasksCompleted: user.totalTasksCompleted + 1
    };
    onUpdateUser(updatedUser);

    // Simulate a successful finish alert
    setTimeout(() => {
      alert(`Parab√©ns! Voc√™ ganhou ${task.reward.toLocaleString('pt-AO')} Kz!`);
      setActiveTask(null);
    }, 500);
  };

  const getTaskColor = (type: TaskType) => {
    switch (type) {
      case TaskType.YOUTUBE: return 'text-red-500 bg-red-500/10';
      case TaskType.TIKTOK: return 'text-white bg-slate-800';
      case TaskType.GOOGLE_ADS: return 'text-amber-500 bg-amber-500/10';
      case TaskType.REELS: return 'text-pink-500 bg-pink-500/10';
      default: return 'text-slate-100 bg-slate-800';
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-6 py-12">
      <div className="text-center mb-16">
        <h1 className="text-4xl font-extrabold mb-4">Mural de Tarefas</h1>
        <p className="text-slate-400">Quanto mais voc√™ assiste, mais voc√™ fatura. Tarefas renovadas a cada 24 horas.</p>
      </div>

      {isWatching && activeTask ? (
        <div className="fixed inset-0 z-[60] bg-slate-950 flex flex-col items-center justify-center p-6">
          <div className="max-w-4xl w-full">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-xl font-bold">{activeTask.title}</h2>
              <div className="bg-amber-500 text-slate-950 font-black px-4 py-2 rounded-lg">
                00:{timeLeft < 10 ? `0${timeLeft}` : timeLeft}
              </div>
            </div>

            <div className="aspect-video w-full bg-black rounded-3xl border-4 border-slate-800 overflow-hidden relative">
              {activeTask.type === TaskType.YOUTUBE ? (
                <iframe 
                  className="w-full h-full"
                  src={`${activeTask.url}?autoplay=1&controls=0&mute=1`}
                  title="Video Task"
                  allow="autoplay"
                ></iframe>
              ) : (
                <div className="w-full h-full flex flex-col items-center justify-center bg-slate-900">
                  <i className={`${activeTask.icon} text-6xl text-amber-500 mb-6 animate-pulse`}></i>
                  <p className="text-slate-400 text-center px-12">Simulando visualiza√ß√£o de an√∫ncio externo... <br/> N√£o feche esta aba para receber seus Kwanzas.</p>
                </div>
              )}
            </div>

            <div className="mt-8 text-center">
              <AdSenseSlot slot="9988776655" format="rectangle" />
              <p className="text-slate-400 italic">O pagamento ser√° creditado automaticamente ao fim do cron√¥metro.</p>
            </div>
          </div>
        </div>
      ) : (
        <>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
            {AVAILABLE_TASKS.map((task, index) => {
              const isCompleted = completedTasks.includes(task.id);
              return (
                <React.Fragment key={task.id}>
                  <div className={`glass p-8 rounded-[32px] transition-all relative group ${isCompleted ? 'opacity-50 grayscale' : 'hover:scale-105 hover:border-amber-500/50'}`}>
                    <div className={`w-14 h-14 rounded-2xl flex items-center justify-center text-2xl mb-6 ${getTaskColor(task.type)}`}>
                      <i className={task.icon}></i>
                    </div>

                    <h3 className="text-xl font-bold mb-2">{task.title}</h3>
                    <p className="text-slate-400 text-sm mb-6">Dura√ß√£o: {task.durationSeconds} segundos</p>

                    <div className="flex justify-between items-center pt-6 border-t border-slate-800">
                      <div className="flex flex-col">
                        <span className="text-xs text-slate-500 font-bold uppercase tracking-wider">Recompensa</span>
                        <span className="text-2xl font-black text-amber-500">{task.reward.toLocaleString('pt-AO')} Kz</span>
                      </div>

                      <button 
                        onClick={() => !isCompleted && startTask(task)}
                        disabled={isCompleted}
                        className={`px-6 py-3 rounded-xl font-bold transition-all ${isCompleted ? 'bg-slate-800 text-slate-500 cursor-not-allowed' : 'gold-gradient text-white shadow-lg group-hover:shadow-amber-500/30'}`}
                      >
                        {isCompleted ? 'Finalizado' : 'Iniciar'}
                      </button>
                    </div>

                    {isCompleted && (
                      <div className="absolute top-4 right-4 text-green-500">
                        <i className="fa-solid fa-circle-check text-2xl"></i>
                      </div>
                    )}
                  </div>
                  {/* Insert an ad slot after every 3 tasks */}
                  {(index + 1) % 3 === 0 && (
                    <div className="col-span-1 md:col-span-2 lg:col-span-3">
                      <AdSenseSlot slot="3344556677" />
                    </div>
                  )}
                </React.Fragment>
              );
            })}
          </div>
          <div className="max-w-4xl mx-auto">
             <AdSenseSlot slot="8877665544" />
          </div>
        </>
      )}
    </div>
  );
};

