interface TasksProps {
  user: User;
  onUpdateUser: (user: User) => void;
}

const Tasks: React.FC<TasksProps> = ({ user, onUpdateUser }) => {
  const [activeTask, setActiveTask] = useState<Task | null>(null);
  const [timeLeft, setTimeLeft] = useState(0);
  const [isWatching, setIsWatching] = useState(false);

  const startTask = (task: Task) => {
    const isVideo = task.type !== TaskType.GOOGLE_ADS;

    // Valida√ß√£o estrita de limites
    if (isVideo && user.dailyVideosCount >= DAILY_LIMITS.VIDEOS) {
      alert(`Limite di√°rio atingido! Voc√™ s√≥ pode assistir ${DAILY_LIMITS.VIDEOS} v√≠deos por dia.`);
      return;
    }
    if (!isVideo && user.dailyAdsCount >= DAILY_LIMITS.ADS) {
      alert(`Limite di√°rio atingido! Voc√™ s√≥ pode ver ${DAILY_LIMITS.ADS} an√∫ncios por dia.`);
      return;
    }

    setActiveTask(task);
    setTimeLeft(task.durationSeconds);
    setIsWatching(true);

    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          completeTask(task);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const completeTask = (task: Task) => {
    setIsWatching(false);
    const isVideo = task.type !== TaskType.GOOGLE_ADS;

    const updatedUser = {
      ...user,
      balance: user.balance + task.reward,
      totalTasksCompleted: user.totalTasksCompleted + 1,
      dailyAdsCount: !isVideo ? user.dailyAdsCount + 1 : user.dailyAdsCount,
      dailyVideosCount: isVideo ? user.dailyVideosCount + 1 : user.dailyVideosCount,
    };

    onUpdateUser(updatedUser);

    setTimeout(() => {
      alert(`Sucesso! Foram adicionados ${task.reward} Kz √† sua carteira.`);
      setActiveTask(null);
    }, 500);
  };

  const getTaskColor = (type: TaskType) => {
    switch (type) {
      case TaskType.YOUTUBE: return 'text-red-500 bg-red-500/10';
      case TaskType.TIKTOK: return 'text-white bg-slate-800';
      case TaskType.GOOGLE_ADS: return 'text-amber-500 bg-amber-500/10';
      case TaskType.REELS: return 'text-pink-500 bg-pink-500/10';
      default: return 'text-slate-100 bg-slate-800';
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-6 py-12">
      <div className="text-center mb-12">
        <h1 className="text-4xl font-extrabold mb-4">Ganhar Kumbu</h1>
