              <div className="flex items-center space-x-5">
                <div className={`w-14 h-14 rounded-2xl flex items-center justify-center text-xl ${
                  task.type === TaskType.GOOGLE_ADS ? 'bg-amber-500/10 text-amber-500' : 'bg-blue-500/10 text-blue-500'
                }`}>
                  <i className={task.icon}></i>
                </div>
                <div>
                  <h3 className="font-bold text-sm">{task.title}</h3>
                  <div className="flex items-center space-x-3 mt-1 text-[10px] font-bold text-slate-500">
                    <span className="flex items-center"><i className="fa-regular fa-clock mr-1"></i> {formatTime(task.durationSeconds)}</span>
                    <span className="flex items-center"><i className="fa-solid fa-shield-check mr-1 text-green-500"></i> Verificado</span>
                  </div>
                </div>
              </div>
              <div className="text-right">
                <p className="text-xl font-black text-white">+{task.reward} <span className="text-xs opacity-50">Kz</span></p>
                {limitReached && <span className="text-[10px] text-red-500 font-bold">Esgotado</span>}
              </div>
            </div>
          );
        })}
      </div>

      {isWatching && activeTask && (
        <div className="fixed inset-0 z-[100] bg-slate-950 flex flex-col p-4">
          <div className="flex justify-between items-center mb-6 pt-4">
             <div className="flex items-center space-x-3">
               <div className="w-8 h-8 rounded-lg gold-gradient flex items-center justify-center text-white text-xs">
                 <i className={activeTask.icon}></i>
               </div>
               <span className="text-sm font-bold">Processando Dep√≥sito</span>
             </div>
             <div className="bg-amber-500 text-slate-950 px-4 py-1.5 rounded-full font-black text-sm">
               {formatTime(timeLeft)}
             </div>
          </div>

          <div className="flex-grow flex flex-col justify-center items-center">
            <div className="w-full aspect-video bg-black rounded-[32px] overflow-hidden shadow-2xl border border-white/5 relative">
              {activeTask.type === TaskType.YOUTUBE ? (
                <iframe 
                  className="w-full h-full pointer-events-none" 
                  src={`${activeTask.url}?autoplay=1&controls=0&mute=1`} 
                  allow="autoplay"
                ></iframe>
              ) : (
                <div className="w-full h-full flex flex-col items-center justify-center bg-slate-900/50 backdrop-blur-xl">
                  <div className="relative mb-8">
                    <div className="absolute inset-0 bg-amber-500 blur-3xl opacity-20 animate-pulse"></div>
                    <i className={`${activeTask.icon} text-7xl text-amber-500 relative z-10`}></i>
                  </div>
                  <p className="text-slate-300 font-bold text-lg animate-pulse">Sincronizando com AdSense...</p>
                  <p className="text-slate-500 text-xs mt-2 px-12 text-center">Aguarde a valida√ß√£o para receber seus {activeTask.reward} Kz.</p>
                </div>
              )}
              <div className="absolute inset-0 z-20"></div>
            </div>

            <button 
              onClick={() => { if(confirm("Cancelar dep√≥sito?")) setIsWatching(false); }}
              className="mt-12 text-slate-500 font-bold text-sm"
            >
              Cancelar e Voltar para Carteira
            </button>
          </div>
        </div>
      )}

      <AdSenseSlot slot="8877665544" />
    </div>
  );
};

export default Tasks;

import React, { useState, useRef } from 'react';
import { User } from '../types';

interface ProfileProps {
  user: User;
  onUpdateUser: (user: User) => void;
}

const Profile: React.FC<ProfileProps> = ({ user, onUpdateUser }) => {
  const [name, setName] = useState(user.name);
  const [phone, setPhone] = useState(user.multicaixaNumber);
  const [isSaving, setIsSaving] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleSave = (e: React.FormEvent) => {
    e.preventDefault();
    setIsSaving(true);

    // Limpar espa√ßos e caracteres n√£o num√©ricos do telefone
    const cleanPhone = phone.replace(/\D/g, '');

    setTimeout(() => {
      onUpdateUser({
        ...user,
        name,
        multicaixaNumber: cleanPhone
      });
      setIsSaving(false);
      alert('Perfil atualizado com sucesso!');
    }, 1000);
  };

  const handleAvatarClick = () => {
    fileInputRef.current?.click();
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      if (file.size > 2 * 1024 * 1024) {
        alert("A imagem √© muito grande. Escolha uma foto com menos de 2MB.");
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        const base64String = reader.result as string;
        onUpdateUser({
          ...user,
          avatar: base64String
        });
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="max-w-4xl mx-auto px-6 py-12">
      <h1 className="text-4xl font-extrabold mb-12">Meu Perfil</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-12">
        <div className="md:col-span-1 text-center">
          <div 
            className="relative inline-block mb-6 group cursor-pointer"
            onClick={handleAvatarClick}
          >
            <img 
              src={user.avatar} 
              alt="Avatar" 
              className="w-40 h-40 rounded-[40px] border-4 border-amber-500 object-cover p-1 shadow-2xl transition-transform group-hover:scale-105" 
            />
            <div className="absolute inset-0 bg-slate-900/40 rounded-[40px] opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center">
               <div className="bg-amber-500 w-10 h-10 rounded-full flex items-center justify-center shadow-lg">
                 <i className="fa-solid fa-camera text-slate-900 text-xl"></i>
               </div>
            </div>
            {/* Hidden File Input */}
            <input 
              type="file" 
              ref={fileInputRef} 
              className="hidden" 
              accept="image/*" 
              onChange={handleFileChange}
            />
          </div>
          <h2 className="text-xl font-bold">{user.name}</h2>
          <p className="text-slate-400 text-sm mb-4">{user.email}</p>
          <div className="bg-green-500/10 text-green-500 px-4 py-1 rounded-full text-xs font-bold inline-block border border-green-500/20">
            Conta Verificada
          </div>
          <p className="mt-4 text-[10px] text-slate-500 uppercase tracking-widest">Clique na foto para alterar</p>
        </div>

        <div className="md:col-span-2 glass p-8 rounded-[32px] border-slate-800">
          <form onSubmit={handleSave} className="space-y-6">
            <div>
              <label className="block text-sm font-medium mb-2 text-slate-300">Nome Completo</label>
              <input 
                type="text" 
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="w-full bg-slate-900 border border-slate-800 rounded-xl p-4 focus:border-amber-500 outline-none transition-all"
                placeholder="Seu nome completo"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2 text-slate-300">E-mail de Cadastro (Bloqueado)</label>
              <input 
                type="email" 
                disabled
                value={user.email}
                className="w-full bg-slate-900/50 border border-slate-800 rounded-xl p-4 text-slate-500 cursor-not-allowed"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2 text-slate-300">N¬∫ MULTICAIXA EXPRESS (9 d√≠gitos)</label>
              <div className="relative">
                <i className="fa-solid fa-credit-card absolute left-4 top-1/2 -translate-y-1/2 text-slate-500"></i>
                <input 
                  type="text" 
                  value={phone}
                  maxLength={9}
                  onChange={(e) => setPhone(e.target.value)}
                  placeholder="Ex: 923123456"
                  className="w-full bg-slate-900 border border-slate-800 rounded-xl py-4 pl-12 pr-4 focus:border-amber-500 outline-none transition-all font-mono"
                />
              </div>
              <p className="text-[10px] text-amber-500/70 mt-2">Importante: Os pagamentos s√£o feitos exclusivamente para este n√∫mero.</p>
            </div>

            <button 
              type="submit"
              disabled={isSaving}
              className="w-full py-4 gold-gradient rounded-xl font-bold text-lg shadow-lg flex items-center justify-center hover:scale-[1.02] active:scale-[0.98] transition-all"
            >
              {isSaving ? <i className="fa-solid fa-spinner animate-spin mr-2"></i> : 'Atualizar Dados'}
            </button>
          </form>

          <div className="mt-12 pt-8 border-t border-slate-800">
            <h3 className="text-red-500 font-bold mb-4">Zona Cr√≠tica</h3>
            <button className="text-sm text-slate-500 hover:text-red-500 flex items-center transition-colors">
              <i className="fa-solid fa-trash-can mr-2"></i> Solicitar encerramento de conta
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
